mapper.select:
SELECT COUNT(*)
		FROM onstar
		WHERE on_num=#{on_num}

		AND rating =#{rating}


		AND rating =#{rating}


		AND rating =#{rating}


		AND rating =#{rating}


		AND rating =#{rating}

SELECT * FROM onstar s JOIN ouser_detail d ON s.user_num = d.user_num LEFT OUTER JOIN ( SELECT onre_num,onstar_num,d.user_num AS writer_num,name AS writer_name, onre_content,onre_date,photo_name as writer_photo FROM onstar_reply r JOIN ouser_detail d ON r.user_num = d.user_num ) r ON s.onstar_num = r.onstar_num WHERE on_num=#{on_num}
AND ROWNUM<=#{rownum}
ORDER BY s.onstar_num DESC
ORDER BY s.rating DESC,s.onstar_num DESC
ORDER BY s.rating,s.onstar_num DESC
